import React, {Component} from "react";
import {connect} from "react-redux";
import {getPosts} from "../redux/fooActions";

class Index extends Component {

    static getInitialProps = async ({store, isServer, pathname, query}) => {
        await store.dispatch(getPosts())
        return {
            custom: "custom"
        }
    }

    handleSubmit = () => {
        this.props.getPosts();
    }

    render() {
        const {custom, foo} = this.props;

        return (
            <React.Fragment>
                <div>Prop from Redux {JSON.stringify(foo)}</div>
                <div>Prop from getInitialProps {custom}</div>
                <button onClick={this.handleSubmit}>Get Data</button>
            </React.Fragment>
        )
    }
}

export default connect(state => state, {getPosts})(Index);
