import axios from "axios";
import { connect } from "react-redux";
import Layout from "../components/Layout";
import { reauthenticate, getCookie, getProfile } from "../redux/authActions";
import Router from "next/router";

const Profile = ({ reauthenticate, getCookie, user, getProfile }) => {
    return (
        <Layout title="Profile Page">
            {(user && (
                <div>
                    <h2>Who am i</h2>
                    {JSON.stringify(user)}
                </div>
            )) ||
                'Please sign in'}
        </Layout>
    )
}

Profile.getInitialProps = async ctx => {
    console.log(ctx);

    if (ctx.isServer) {
        if (ctx.req.headers.cookie) {
            const token = getCookie('token', ctx.req);
            ctx.store.dispatch(reauthenticate(token));
        } else {
            const token = ctx.store.getState().authentication.token;

            if (token && (ctx.pathname === '/signin' || ctx.pathname === '/signup')) {
                setTimeout(function () {
                    Router.push('/');
                }, 0);
            }
        }

        const token = ctx.store.getState().authentication.token;

        if (token) {
            return {
                user: 'Ryan'
            };
        }
    }

}

export default connect(state => state, { reauthenticate, getProfile })(Profile);
